import { Injectable } from '@angular/core';
import { Observable, Subscriber } from 'rxjs';
import { map } from 'rxjs/operators';
import { ChatDB } from '../../shared/data/chat/chat';
import { ChatMessages, ChatUsers } from '../model/chat.models';
import { HttpClient,HttpHeaders  } from '@angular/common/http';

var today = new Date().toLocaleString('en-US', { hour: 'numeric', minute: 'numeric', hour12: true });

@Injectable({
  providedIn: 'root'
})
export class ChatService {

  public observer: Subscriber<{}>;
  public chat: any[] = []
  public users: any[] = []
  public chatHistory : ChatMessages[] = []
  public pastOpenchat: ChatMessages[] = []
  readonly base="https://localhost:44372/api/GPTStock"
  _returnedChatGPTResponse: string="";
  listChatsMessagesInService: any[]=[]; 
  listChats: ChatMessages[]=[];

  constructor(private http:HttpClient) {
    this.chat = ChatDB.chat
    this.users = ChatDB.chatUser
  }

  //formChat:ChatMessages = new ChatMessages();


  // Get User Data
  public getUsers(): Observable<ChatUsers[]> {
    const users = new Observable(observer => {
      observer.next(this.users);
      observer.complete();
    });
    return <Observable<ChatUsers[]>>users;
  }

  // Get cuurent user
  public getCurrentUser() {
    return this.getUsers().pipe(map(users => {
      return users.find((item) => {
        return item.authenticate === 0;
      });
    }));
  }

  // chat to user
  public chatToUser(id: number) {
    return this.getUsers().pipe(map(users => {
      return users.find((item) => {
        return item.id === id;
      });
    }));
  }

  // Get users chat

  public getChats(id:any){
    this.http.get(this.base + "/getmessages/" + id).toPromise().then(res=> this.chatHistory = res as ChatMessages[]);
  }


  // public getUserChat(): Observable<ChatMessages[]> {
  //   const chat = new Observable(observer => {
  //     observer.next(this.chat);
  //     observer.complete();
  //   });
  //   return <Observable<ChatMessages[]>>chat;
  // }

  // // Get chat History
  // public getChatHistory(id: number) {
  //   return this.getUserChat().pipe(map(users => {
  //     return users.find((item) => {
  //       return item.userId === id;
  //     });
  //   }));
  // }

  // Send Message to user
  public sendMessage(chat:ChatMessages, jsonData){
        setTimeout(function () {
          document.querySelector(".chat-history").scrollBy({ top: 200, behavior: 'smooth' });
        }, 310)
       this.responseMessage(chat,jsonData);
  }

  wait(ms: number) {
    return new Promise((resolve) => setTimeout(resolve, ms));
  }

  public async responseMessage(chat:ChatMessages,jsonData) {
    const headers = new HttpHeaders({'Content-Type': 'application/json'});
    this.http.post<any>(this.base+"/stocktable/" + chat.messages[chat.messages.length-1][0].question , jsonData , { headers: headers})
    .subscribe(result => 
      { this._returnedChatGPTResponse = result;}, 
      error => console.error(error)); 

      setTimeout(()=> {
        this.listChats = [{
          chatMessageId:0,
          userId: 1,
          messages:[] 
          }];

          this.listChatsMessagesInService=[{
            messageId: 0,
            userId: 1,
            question: chat.messages[0].question,
            answer: this._returnedChatGPTResponse
          }]
          chat.messages.push(this.listChatsMessagesInService);
         // const lastColumnIndex = this.listChats.length - 1;
          // const lastIndexMessages = this.listChats[0].messages.length -1;
          // const lastAnswer = this.listChats[0].messages[lastIndexMessages];
          // lastAnswer.answer = this._returnedChatGPTResponse;
          //chat.messages[chat.messages.length-1][3].answer = this._returnedChatGPTResponse;
          console.log(this._returnedChatGPTResponse + " edennmom ");
          console.log(this.listChats.messages[chat.messages.length-1][chat.messages.length-2].answer);

          document.querySelector(".chat-history").scrollBy({ top: 200, behavior: 'smooth' });
      }, 3000);

      setTimeout(()=> {
        this.http.post(this.base + "/postmessage" , this.listChats).subscribe( error => console.error(error));
        this.listChats=[];
      },30000)


  }

  public getMessageByChatId(id:any){
    return this.http.get(this.base + "/getmessagesbychatId/" + id).toPromise().then(res=> this.pastOpenchat = res as ChatMessages[]);
  }

}
